t = time.clock()
data = zip(c, n)
x, n = CRT(data)
realnum = gmpy2.iroot(gmpy2.mpz(x), e)[0].digits()
print ('[-]m is: ' + realnum)
print('[!]Timer:',round(time.clock() -t ,2),'s')
print ('[!]All Done!')
